import axios from "axios";
import { useEffect, useState } from "react";
// import Genres from "../../components/Genres/Genres";
import CustomPagination from "./pagination/CustomPagination";
import Sidebar from "./Sidebar";
import SingleContent from "./SingleContent/SingleContent";
// import useGenre from "../../hooks/useGenre";

const Series = () => {
    //   const [genres, setGenres] = useState([]);
    //   const [selectedGenres, setSelectedGenres] = useState([]);
    const [page, setPage] = useState(1);
    const [content, setContent] = useState([]);
    const [numOfPages, setNumOfPages] = useState();
    //   const genreforURL = useGenre(selectedGenres);

    const fetchSeries = async () => {
        const { data } = await axios.get(
            `https://api.themoviedb.org/3/discover/tv?api_key=d2be8d71bbce316ce6cf341ced96a652&language=en-US&sort_by=popularity.desc&include_adult=false&include_video=false&page=1&with_watch_monetization_types=flatrate&page=${page}`
        );
        setContent(data.results);
        setNumOfPages(data.total_pages);
        // console.log(data);
    };

    useEffect(() => {
        window.scroll(0, 0);
        fetchSeries();
        // eslint-disable-next-line
    }, [genreforURL, page]);

    return (
        <>
            <div>
                <span className="pagetitle">Discover Series</span>
                {/* <Genres
        type="tv"
        selectedGenres={selectedGenres}
        setSelectedGenres={setSelectedGenres}
        genres={genres}
        setGenres={setGenres}
        setPage={setPage}
      /> */}
                <div className="trending">
                    {content &&
                        content.map((c) => (
                            <SingleContent
                                key={c.id}
                                id={c.id}
                                poster_path={c.poster_path}
                                title={c.title || c.name}
                                date={c.first_air_date || c.release_date}
                                media_type="tv"
                            />
                        ))}
                </div>
                {numOfPages > 1 && (
                    <CustomPagination setPage={setPage} numOfPages={numOfPages} />
                )}
            </div>
            <Sidebar />
        </>

    );
};

export default Series;
