import React, { useEffect } from 'react'

const Genres =({
    selectedGenres,
    setSelectedGenres,
    genres,
    setGenres,
    type,
    setPage,
}) => {  
    
    const fetchGeneres = async () => {
       const {data} = await axios.get(`api.themoviedb.org/3/genre/${type}/list?api_key=d2be8d71bbce316ce6cf341ced96a652&language=en-US`);

       setGenres(data.genres);
    };

    console.log(genres);

    useEffect(() => {
      fetchGeneres();

      return () => {
        setGenres({});
      }
    }, [])

    return (
    <div>Genres</div>
  )
}

