import * as React from 'react';
import { useState, useEffect } from 'react';
import Backdrop from '@mui/material/Backdrop';
import Box from '@mui/material/Box';
import Modal from '@mui/material/Modal';
import Fade from '@mui/material/Fade';
import Button from '@mui/material/Button';
import axios from 'axios'
import { img_500, unavailable } from '../config';
import "../css.contentmodal.css"

const style = {
    position: 'absolute',
    top: '50%',
    left: '50%',
    transform: 'translate(-50%, -50%)',
    width: 400,
    boxShadow: 24,
    p: 4,
    width: "90%",
    height: "80%",
    backgroundColor: "rgb(77, 69, 69)",
    border: "1px solid #282c34",
    borderRadius: 10,
    color: "white",
    // boxShadow: theme.shadows[5],
    // padding: theme.spacing(1, 1, 3),
};

export default function ContentModal({ children, media_type, id }) {
    const [open, setOpen] = React.useState(false);
    const [content, setContent] = React.useState();
    const [video, setvideo] = useState();

    const handleOpen = () => setOpen(true);
    const handleClose = () => setOpen(false);

    const fetchdata = async () => {
        const { data } = await axios.get(
            `https://api.themoviedb.org/3/${media_type}/${id}?api_key=d2be8d71bbce316ce6cf341ced96a652&language=en-US`

        );
        setContent(data);
    };
    const fetchVideo = async () => {
        const { data } = await axios.get(
            `https://api.themoviedb.org/3/${media_type}/${id}/videos?api_key=d2be8d71bbce316ce6cf341ced96a652&language=en-US`
        );
        console.log(data);
        setvideo(data.results[0]?.key);
    };

    useEffect(() => {
        fetchdata();
        fetchVideo();
    }, [])

    return (
        <div>
            <Button className='media' onClick={handleOpen}>{children}</Button>
            <Modal
                aria-labelledby="transition-modal-title"
                aria-describedby="transition-modal-description"
                open={open}
                onClose={handleClose}
                closeAfterTransition
                BackdropComponent={Backdrop}
                BackdropProps={{
                    timeout: 500,
                }}
            >
                <Fade in={open}>
                    <Box sx={style}>
                        {/* <div className='ContentModal'>
                            <img className='Content_potrait' src={content.poster_path ? `${img_500}/${content.poster_path}` : unavailable} alt="d" />
                        </div> */}
                        <div className="ContentModal__about">
                            <span className="ContentModal__title">
                                {content.name || content.title} (
                                {(
                                    content.first_air_date ||
                                    content.release_date ||
                                    "-----"
                                ).substring(0, 4)}
                                )
                            </span>
                            {content.tagline && (
                                <i className="tagline">{content.tagline}</i>
                            )}

                            <span className="ContentModal__description">
                                {content.overview}
                            </span>

                            {/* <div>
                    <Carousel id={id} media_type={media_type} />
                  </div> */}

                            <Button
                                variant="contained"
                                startIcon="hjhgh"
                                color="secondary"
                                target="__blank"
                                href={`https://www.youtube.com/watch?v=${video}`}
                            >
                                Watch the Trailer
                            </Button>
                        </div>
                    </Box>
                </Fade>
            </Modal>
        </div>
    );
}
