import React from 'react'
import axios from 'axios';
import { useState, useEffect } from 'react';
import SingleContent from './SingleContent/SingleContent';

function Trending() {
    const [content,setContent] = useState([]);

    const fetchTrending = async () =>{
        const {data} = await axios.get('https://api.themoviedb.org/3/trending/all/day?api_key=d2be8d71bbce316ce6cf341ced96a652');
        setContent(data.results);  
    };

    useEffect(() => {
        fetchTrending();
    }, []);

  return (
    <>
        <h1 className='pagetitle'>Trending</h1>
        {content && content.map((c) => <SingleContent key={c.id}/>)}  {/*if there is n0 content then it shows error*/}
    </>
  )
} 

export default Trending
