import { Chip } from '@mui/material';
import axios from 'axios'
import React, { useEffect } from 'react'
import Movies from './Movies';

const Genres = ({
  selectedGenres,
  setSelectedGenres,
  genres,
  setGenres,
  type,
  setPage,
}) => {

  const fetchGeneres = async () => {
    const { data } = await axios.get(`api.themoviedb.org/3/genre/${type}/list?api_key=d2be8d71bbce316ce6cf341ced96a652&language=en-US`);

    setGenres(data.genres);
  };

  useEffect(() => {
    fetchGeneres();

    return () => {
      setGenres({});
    }
  }, [])

  return (
    <div style={{ padding: "66px 218px" }}>
      
        <Chip label={genres.name}/>
    
      
    </div>
  )
}

export default Genres
